    private void jButton_signupActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_signupActionPerformed
        // TODO add your handling code here:
        String Gfname= jTextField1.getText () ;
        String uname= jTextField2.getText () ;
   // String phone= jTextField3.getText () ;
        String pass1 = String.valueOf(jPasswordField1.getPassword() ) ;
        String pass2 = String.valueOf(jPasswordField2.getPassword()) ;
        
        if(verifyFields()){
            if(!checkUsername(uname)){
                PreparedStatement ps;
                ResultSet rs;
                String SignUp_Query = "INSERT INTO `hms`.`signup` (`UserName`, `pass_`, `Fullname`) VALUES (?, ?, ?);";
               try{
                ps=MyConnection.getConnection().prepareStatement(SignUp_Query);
                ps.setString(1,Gfname);
                ps.setString(2,pass1);
                ps.setString(3,uname);
                 
                if(ps.executeUpdate() != 0){
                    JOptionPane.showMessageDialog(rootPane, "Your Account has been Created");
                }else{
                    JOptionPane.showMessageDialog(rootPane, "Error: Your Account has not been Created");
                }
                
                               
               }catch(Exception e){
                System.out.println(e);
            }
                        
            }
        }
    }//GEN-LAST:event_jButton_signupActionPerformed

