   public boolean verifyFields (){
    String Gfname= jTextField1.getText () ;
    String uname= jTextField2.getText () ;
   // String phone= jTextField3.getText () ;
    String passl = String.valueOf(jPasswordField1.getPassword() ) ;
    String pass2 = String.valueOf(jPasswordField2.getPassword()) ;
    // check empty fields
    if (Gfname.trim ().equals("") || uname.trim().equals("")  ||  passl.trim().equals("") || pass2.trim().equals ("") ){
    JOptionPane.showMessageDialog(null, "One Or More Fields Are Empty", "Empty Fields", 2) ;
    return true;
    }
    //check if the two password are equals
    else if (passl.equals(pass2) == false ){
    JOptionPane.showMessageDialog(null, "Password Doesn't Match", "Confirm Password", 2);
    return false;
    }
    else {
        return true;
    }
}
   
 public boolean checkUsername(String username){
    PreparedStatement st;
    ResultSet rs;
    boolean username_exist= false;
    String query = "SELECT * FROM hms.signup where UserName = ?";
    try{
    
    st = MyConnection.getConnection().prepareStatement(query) ;
    st.setString(1, username) ;
    rs = st.executeQuery();
    if(rs.next()){
    username_exist = true;
    JOptionPane.showMessageDialog(rootPane, "This Username is Already Taken , Choose Another Name!","Username Exist",2);
    }
   
    }catch (SQLException ex){
    System.out.println(ex);
    }
    return username_exist;}
